import OAuthInfo from '@arcgis/core/identity/OAuthInfo.js';
import Portal from '@arcgis/core/portal/Portal.js';

/**
 * Register application ID and Portal URL
 * with the IdentityManager
 * @param appId
 * @param portalUrl
 * @returns Promise<void>
