    let kml = {
      Document: {
        Folder: {
          name: status,
          Placemark: folders[status].map(feature => tokml(feature))
        }
      }
    };

    let xml = parse('kml', kml, {format: true});
    downloadKML(xml, `${status}.kml`);
  }
}

function downloadKML(xml, filename) {
  let blob = new Blob([xml], {type: 'application/vnd.google-earth.kml+xml'});
  let url = URL.createObjectURL(blob);
  let link = document.createElement('a');
  link.href = url;
  link.download = filename;
  link.click();
}
```

