  const usersGISCollection = db.collection('usersGIS');

  // Get all users from the arcgisUsers collection
  const arcgisUsers = await arcgisUsersCollection.find().toArray();

  // Get the usernames of all arcgisUsers
  const arcgisUsernames = arcgisUsers.map(user => user.username);

